import data from "./data.json";
import ListItem from "@material-ui/core/ListItem";
// import ListItemText from "@material-ui/core/ListItemText";
import List from "@material-ui/core/List";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";

const useStyles = makeStyles(() => ({
  heading: {
    padding: "10px",
    marginBottom: "10px",
    boxShadow: "0 0 0 0",
    borderRadius: "0px",
  },
  title: {
    textAlign: "center",
  },
  tableHeading: {
    marginBottom: "10px",
    marginTop: "10px",
    borderRadius: "0px",
    boxShadow: "0 0 0 0",
  },
}));

function App(props) {
  const classes = useStyles(props);
  function sortUpVotes(sortBy = "desc") {
    let list = [];
    if (sortBy === "desc")
      list = data.sort((a, b) => parseFloat(b.upVotes) - parseFloat(a.upVotes));
    else
      list = data.sort((a, b) => parseFloat(a.upVotes) - parseFloat(b.upVotes));
  }
  function sortByDate(sortBy = "desc") {
    let list = [];
    if (sortBy === "desc")
      list = data.sort(function (a, b) {
        return new Date(a.date) - new Date(b.date);
      });
    else
      list = data.sort(function (a, b) {
        return new Date(b.date) - new Date(a.date);
      });
  }
  return (
    <div>
      <Grid container>
        <Grid item xs={12}>
          <Paper elevation={4}>
            <Typography className={classes.heading}>
              Sorting Articles
            </Typography>
          </Paper>
        </Grid>
        {/* <Paper item> */}
        <Grid container>
          <Grid item xs={4}  className={classes.tableHeading}>
            <Paper className={classes.title}>Title</Paper>
          </Grid>
          <Grid item xs={4}>
            <Paper className={classes.title}>UpVotes</Paper>
          </Grid>
          <Grid item xs={4}>
            <Paper className={classes.title}>Date</Paper>
          </Grid>
        </Grid>
        {/* </Paper> */}
        {data.map((item, index) => (
          <Grid container key={index}>
            <Grid item xs={4}>
              <Typography className={classes.title}>{item.title}</Typography>
            </Grid>
            <Grid item xs={4}>
              <Typography className={classes.title}>{item.upVotes}</Typography>
            </Grid>
            <Grid item xs={4}>
              <Typography className={classes.title}>{item.date}</Typography>
            </Grid>
          </Grid>
        ))}
      </Grid>
    </div>
  );
}

export default App;
