import logo from './logo.svg';
import './App.css';
import data from './data.json';
function App() {
	console.log('data', new Date(data[0].date).getDate());
	function sortUpVotes(sortBy = 'desc') {
		let list = [];
		if (sortBy === 'desc') list = data.sort((a, b) => parseFloat(b.upVotes) - parseFloat(a.upVotes));
		else list = data.sort((a, b) => parseFloat(a.upVotes) - parseFloat(b.upVotes));
	}
	function sortByDate(sortBy = 'desc') {
		let list = [];
		if (sortBy === 'desc')
		  list = data.sort(function (a, b) {
			return new Date(a.date) - new Date(b.date);
		  });
		else
		  list = data.sort(function (a, b) {
			return new Date(b.date) - new Date(a.date);
		  });
	  }
	return (
		<div className="App">
			<header className="App-header">
				<img src={logo} className="App-logo" alt="logo" />
				<p>
					Edit <code>src/App.js</code> and save to reload.
				</p>
				<a className="App-link" href="https://reactjs.org" target="_blank" rel="noopener noreferrer">
					Learn React
				</a>
			</header>
		</div>
	);
}

export default App;
