import logo from "./logo.svg";
import "./App.css";
import data from "./data.json";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import List from "@material-ui/core/List";
import { Typography } from "@material-ui/core";
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
function App() {
  console.log("data", new Date(data[0].date).getDate());
  function sortUpVotes(sortBy = "desc") {
    let list = [];
    if (sortBy === "desc")
      list = data.sort((a, b) => parseFloat(b.upVotes) - parseFloat(a.upVotes));
    else
      list = data.sort((a, b) => parseFloat(a.upVotes) - parseFloat(b.upVotes));
  }
  function sortByDate(sortBy = "desc") {
    let list = [];
    if (sortBy === "desc")
      list = data.sort(function (a, b) {
        return new Date(a.date) - new Date(b.date);
      });
    else
      list = data.sort(function (a, b) {
        return new Date(b.date) - new Date(a.date);
      });
  }
  return (
    <div>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Paper>
            <Typography>Sorting Articles</Typography>
          </Paper>
        </Grid>
        <Grid item xs={6}>
          <Paper>xs=6</Paper>
        </Grid>
        <Grid item xs={6}>
          <Paper>xs=6</Paper>
        </Grid>
        <Grid item xs={3}>
          <Paper>xs=3</Paper>
        </Grid>
        <Grid item xs={3}>
          <Paper>xs=3</Paper>
        </Grid>
        <Grid item xs={3}>
          <Paper>xs=3</Paper>
        </Grid>
        <Grid item xs={3}>
          <Paper>xs=3</Paper>
        </Grid>
      </Grid>
      <List>
        <div>
          <Typography>Title</Typography>
          <Typography>UpVotes</Typography>
          <Typography>Date</Typography>
        </div>
        {data.map((item, index) => (
          <ListItem key={index}>
            <Typography>{item.title}</Typography>
            <Typography>{item.upVotes}</Typography>
            <Typography>{item.date}</Typography>
          </ListItem>
        ))}
      </List>
    </div>
  );
}

export default App;
