import data from "./data.json";
import ListItem from "@material-ui/core/ListItem";
// import ListItemText from "@material-ui/core/ListItemText";
import List from "@material-ui/core/List";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";

const useStyles = makeStyles(() => ({
  heading: {
    margin: "10px",
  },

}));

function App(props) {
  const classes = useStyles(props);
  function sortUpVotes(sortBy = "desc") {
    let list = [];
    if (sortBy === "desc")
      list = data.sort((a, b) => parseFloat(b.upVotes) - parseFloat(a.upVotes));
    else
      list = data.sort((a, b) => parseFloat(a.upVotes) - parseFloat(b.upVotes));
  }
  function sortByDate(sortBy = "desc") {
    let list = [];
    if (sortBy === "desc")
      list = data.sort(function (a, b) {
        return new Date(a.date) - new Date(b.date);
      });
    else
      list = data.sort(function (a, b) {
        return new Date(b.date) - new Date(a.date);
      });
  }
  return (
    <div>
      <Grid container spacing={3} className={classes.heading}>
        <Grid item xs={12}>
          <Paper>
            <Typography>Sorting Articles</Typography>
          </Paper>
        </Grid>
        <Grid container xs={12}>
          <Grid item xs={4}>
            <Typography>Title</Typography>
          </Grid>
          <Grid item xs={4}>
            <Typography>UpVotes</Typography>
          </Grid>
          <Grid item xs={4}>
            <Typography>Date</Typography>
          </Grid>
        </Grid>
        {data.map((item, index) => (
          <Grid container key={index} xs={12}>
            <Grid item xs={4}>
              <Typography>{item.title}</Typography>
            </Grid>
            <Grid item xs={4}>
              <Typography>{item.upVotes}</Typography>
            </Grid>
            <Grid item xs={4}>
              <Typography>{item.date}</Typography>
            </Grid>
          </Grid>
        ))}
      </Grid>
    </div>
  );
}

export default App;
