import React from "react";
import data from "./data.json";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";

const useStyles = makeStyles(() => ({
  heading: {
    padding: "20px",
    marginBottom: "10px",
    boxShadow: "0 0 0 0",
    borderRadius: "0px",
  },
  title: {
    textAlign: "center",
    marginTop: "10px",
    borderRadius: "0px",
    boxShadow: "0 0 0 0",
    paddingTop: "10px",
    paddingBottom: "10px",
    backgroundColor: "#ef7e7e",
  },
  listItem: { textAlign: "center", marginTop: "10px" },
  button: { margin: "10px" },
}));

function App(props) {
  const classes = useStyles(props);
  function sortUpVotes(sortBy = "desc") {
    let list = [];
    if (sortBy === "desc")
      list = data.sort((a, b) => parseFloat(b.upVotes) - parseFloat(a.upVotes));
    else
      list = data.sort((a, b) => parseFloat(a.upVotes) - parseFloat(b.upVotes));
  }
  function sortByDate(sortBy = "desc") {
    let list = [];
    if (sortBy === "desc")
      list = data.sort(function (a, b) {
        return new Date(a.date) - new Date(b.date);
      });
    else
      list = data.sort(function (a, b) {
        return new Date(b.date) - new Date(a.date);
      });
  }
  return (
    <div>
      <Grid container>
        <Grid item xs={12}>
          <Paper elevation={4}>
            <Grid container justify="flex-end">
              <Grid item xs={12} sm={8} md={8} lg={10}>
                <Typography className={classes.heading}>
                  Sorting Articles
                </Typography>
              </Grid>
              <Grid item sm={2} xs={6} md={2} lg={1}>
                <Button
                  onClick={() => sortUpVotes()}
                  color="primary"
				  variant="contained"
				  className={classes}
                >
                  UpVotes
                </Button>
              </Grid>
              <Grid item xs={6} sm={2} md={2} lg={1}>
                <Button
                  onClick={() => sortByDate()}
                  color="secondary"
                  variant="contained"
                >
                  Date
                </Button>
              </Grid>
            </Grid>
          </Paper>
        </Grid>
        <Grid container>
          <Grid item xs={4}>
            <Paper className={classes.title}>Title</Paper>
          </Grid>
          <Grid item xs={4}>
            <Paper className={classes.title}>UpVotes</Paper>
          </Grid>
          <Grid item xs={4}>
            <Paper className={classes.title}>Date</Paper>
          </Grid>
        </Grid>
        {data.map((item, index) => (
          <Grid container key={index}>
            <Grid item xs={4}>
              <Typography className={classes.listItem}>{item.title}</Typography>
            </Grid>
            <Grid item xs={4}>
              <Typography className={classes.listItem}>
                {item.upVotes}
              </Typography>
            </Grid>
            <Grid item xs={4}>
              <Typography className={classes.listItem}>{item.date}</Typography>
            </Grid>
          </Grid>
        ))}
      </Grid>
    </div>
  );
}

export default App;
